insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (997, null, 'EVAP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Vinblastine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Vinblastine:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug.</li></ul>', 'High', null, null, null, '<p><strong>Etoposide:</strong> 120 mg/m2 IV on days 1,8, and 15<br><strong>Vinblastine:</strong> 4 mg/m2 IV on days 1,8, and 15<br><strong>Cytarabine: </strong>30 mg/m2 IV on days 1, 8, and 15<br><strong>Cisplatin:</strong> 40 mg/m2 IV on days 1, 8, and 15<br>Repeat cycle every 28 days.</p>', null);
