insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1114, null, 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (total bilirubin &lt;1 to 1.5 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (total bilirubin &gt;1.5 times ULN): Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Pembrolizumab:</strong> 10 mg/kg IV on day 1<br>Repeat cycle every 2 weeks</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (852, null, 'CHOP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Cyclophosphamide:</strong> 750 mg/m2/day IV, days 1, 22<br><strong>Doxorubicin:</strong> 40 mg/m2/day IV, days 1, 22<br><strong>Vincristine:</strong> 1.5 mg/m2 IV weekly for 6 doses<br><strong>Prednisone:</strong> 40 mg/m2/day PO for 28 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (853, null, 'SNCCL/B-ALL', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL: 50% of usual dosage.<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL: Do not use</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 5 mg/dL: Do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 3 mg/dL: 50% of usual dosage.</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: 50% of usual dosage.<br>If bilirubin 3.1-5 mg/dL: 25% of usual dosage.<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min: 75% of usual dosage.<br>If CrCl &lt; 10 mL/min: 50% of usual dosage.</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min: 50% of normal dosage.<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><h3>&nbsp;</h3>', 'High', null, null, null, '<p>INITIAL PHASE<br><strong>Cytarabine:</strong> 50 mg/m2/day (maximum, 50 mg/d) IT, days 1, 2, 3, 11<br><strong>Methotrexate:</strong> 12 mg/m2/day (maximum, 12 mg/d) IT, days 4, 11<br><strong>Cyclophosphamide:</strong> 300 mg/m2 IV push every 12 hours for 6 doses, days 1-3<br><strong>Doxorubicin:</strong> 50 mg/m2 IV, day 4<br><strong>Vincristine:</strong> 1.5 mg/m2/day IV, days 4, 11<br>Alternating with:<br><br>SECONDARY PHASE<br><strong>Cytarabine:</strong> 50 mg/m2 (maximum, 50 mg) IT, day 1<br><strong>Methotrexate:</strong> 12 mg/m2 (maximum, 12 mg) IT, day 1<br><strong>Methotrexate:</strong> 200 mg/m2 IV bolus, then 800 mg/m2 IV continuous infusion<br><strong>Cytarabine:</strong> 3,000 mg/m2 IV over 3 hours every 12 hours for 4 doses, days 2, 3<br><strong>Leucovorin calcium:</strong> 30 mg/m2 IV at hour 42, then 3 mg/m2 IV at hours 54, 66, 78</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (854, null, 'Vincristine + Asparaginase + Dexamethasone + MTX', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Asparaginase:</strong>&nbsp;<br>Use contraindicated in patients with severe hepatic impairment.</li><li><strong>Dexamethasone:</strong>&nbsp;<br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Prednisone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Asparaginase:</strong>&nbsp;<br>No dose reduction</li><li><strong>Dexamethasone:</strong>&nbsp;<br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Prednisone:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Vincristine:</strong> 1.5 mg/m2 IV, days 0,7, 14, 21<br><strong>Asparaginase:</strong> 6,000 units/m2 IM, 3 times weekly for 9 doses, starting on days 2-4<br><strong>Dexamethasone:</strong> 6 mg/m2/day PO in 3 equal doses (was substituted for prednisone)<br><strong>Methotrexate:</strong> 8, 10, or 12 mg IT, age-adjusted, days 0, 14 (also days 7, 21 for patients with CNS disease at diagnosis)<br><strong>Prednisone:</strong> 40 mg/m2/day PO</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (312, 'Carboplatin : Paraplatin<br>Etoposide : VP-16<br>Ifosfamide : Ifex', 'ICE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Etoposide</strong>:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose by 25%</li><li><strong>Carboplatin</strong>:<br>Dosing based on&nbsp;<strong>mg/m2</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min:&nbsp;Reduce dose by 50%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min:&nbsp;Reduce dose by 75%</li><li><strong>Etoposide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', '<b>ICE</b>', 'Cairo MS. The use of ifosfamide, carboplatin, and etoposide in children with solid tumors. $Semin Oncol. 1995;22(3 suppl 7):23-27.$', null, '<p><strong>Ifosfamide:</strong> 1,800 mg/m2/day IV over 1 hour, days 1 to 5<br><strong>Carboplatin:</strong> 400 mg/m2/day IV over 1 hour, days 1 and 2<br><strong>Etoposide: </strong>100 mg/m2/day IV over 1 hour, days 1 to 5</p><p><strong>Filgrastim:</strong> 5 to 10 mcg/kg/day SC, days 6 to 18 if ANC ≥1,000/mm3 or until post nadir ANC ≥1,000/mm3</p><p>Repeat cycle every 21 days for up to 12 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (314, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Vincristine : Oncovin, VCR', 'P6', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥10 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose by 25%</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', '<b>P6</b>', 'Kolb EA, Kushner BH, Gorlick R, et al. Long-term event-free survival after intensive chemotherapy for Ewing''s family of tumors in children and young adults. $J Clin Oncol. 2003;21(18):3423-3430.$', null, '<p>CYCLES 1, 2, 3 AND 6<br><strong>Cyclophosphamide:</strong> 2,100 mg/m2/day IV, days 1, 2<br><strong>Doxorubicin:</strong> 25 mg/m2 IV continuous infusion, days 1-3<br><strong>Vincristine:</strong> 0.67 mg/m2 (maximum, 2 mg/cycle) continuous infusion, days 1-3<br><br>CYCLES 4, 5 AND 7<br><strong>Ifosfamide:</strong> 1,800 mg/m2/day IV, days 1-5<br><strong>Etoposide:</strong> 100 mg/m2/day IV, days 1-5</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (856, null, 'Doxorubicin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Doxorubicin</strong>:<br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Doxorubicin:</strong> 60 mg/m2 IV on day 1<br>Repeat cycle every 21 days.<br>&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (863, null, 'Bortezomib + Dexamethasone + Rituximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin&nbsp;1-1.5 times upper normal limit: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin&nbsp;&gt;1.5 times upper normal limit: Reduce the initial dose to 0.7 mg/m2. &nbsp; &nbsp;</li><li><strong>Dexamethasone:</strong><br>No dose reduction &nbsp; &nbsp; &nbsp;&nbsp;</li><li><strong>Rituximab:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li><li><strong>Rituximab:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Bortezomib:</strong> 1.3 mg/m2 IV on days 1, 4, 8, and 11<br><strong>Dexamethasone:</strong> 40 mg IV on days 1, 4, 8, and 11<br><strong>Rituximab:</strong> 375 mg/m2 IV on day 11<br>Repeat for 4 cycles as induction therapy followed by a 12-week break and then 4<br>additional cycles, each given 12 weeks apart.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (869, null, 'CAP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide</strong>:<br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cisplatin</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug.</li></ul>', 'High', null, null, null, '<p><strong>Cyclophosphamide: </strong>500 mg/m2 IV on day 1<br><strong>Doxorubicin:</strong> 50 mg/m2 IV on day 1<br><strong>Cisplatin: </strong>80 mg/m2 IV on day&nbsp;1<br>Repeat cycle every 21 days.</p>', null);
