insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (941, null, 'DHAP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Dexamethasone:</strong><br>No dose reduction</li><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Dexamethasone:</strong><br>No dose reduction</li><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin:</strong> 100 mg/m2 IV continuous infusion over 24 hours, day 1<br><strong>Cytarabine:</strong> 2,000 mg/m2 IV over 3 hours every 12 hours for 2 doses on day 2 after completion of cisplatin infusion. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Dexamethasone:</strong> 40 mg/day PO or IV on days 1-4<br>Repeat cycle every 21-28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (942, null, 'ICE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Carboplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% dosage.</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Ifosfamide:</strong> 5,000 mg/m2 IV continuous infusion over 24 hours on day 2<br><strong>Etoposide:</strong> 100 mg/m2 IV on days 1-3</p><p><strong>Carboplatin:</strong> AUC of 5 (maximum 800 mg) IV on day 2<br><strong>Mesna:</strong> 5,000 mg/m2 IV in combination with ifosfamide dose.<br>Repeat every 14 days. G-CSF support is administered at 5 μg/kg/day on days 5-12.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (943, null, 'RICE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>No dose reduction</li><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>No dose reduction</li><li><strong>Carboplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% dosage.</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Rituximab:</strong> 375 mg/m2 IV on day 1<br><strong>Ifosfamide:</strong> 5,000 mg/m2 IV continuous infusions for 24 hours, day 4<br><strong>Carboplatin:</strong> AUC of 5 (maximum 800 mg) IV, day 4<br><strong>Etoposide:</strong> 100 mg/m2 IV, days 3-5</p><p><strong>Mesna:</strong> 5,000 mg/m2 IV in combination with ifosfamide dose.<br>Repeat every 14 days.&nbsp;<br>Repeat cycle every 14 days. Rituximab is also given at 48 hours before the<br>start of the first cycle. G-CSF is administered at 5 μg/kg/day SC on days 7-14 after<br>the first 2 cycles and at 10 μg/kg/day SC after the third cycle.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (954, null, 'Ibritumomab Tiuxetan', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ibritumomab tiuxetan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustments not provided by the manufacturer</li><li><strong>Rituximab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ibritumomab tiuxetan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustments not provided by the manufacturer</li><li><strong>Rituximab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Rituximab:</strong> 250 mg/m2 IV on days 1 and 8<br><strong>111 In-Ibritumomab tiuxetan:</strong> 5 mCi of 111 In, 1.6 mg of ibritumomab tiuxetan IV on day 1<br><strong>90 Y-Ibritumomab tiuxetan: </strong>0.4 mCi/kg IV over 10 min on day 8 after the day 8 rituximab dose<br>The dose of 90 Y-ibritumomab tiuxetan is capped at 32 mCi.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (955, null, 'Fludarabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Fludarabine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Fludarabine:</strong><br>If CrCl 10-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt;10 mL/min: Reduce dose by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Fludarabine:</strong> 25 mg/m2 IV on days 1-5<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (956, null, 'Cladribine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cladribine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> Dosage adjustments not provided by the manufacturer&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cladribine:</strong><br>If CrCl 10-50 mL/min: Dose reduction by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;10 mL/min: Dose reduction by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Cladribine:</strong> 0.5-0.7 mg/kg SC on days 1-5 or 0.1 mg/kg IV on days 1-7<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (957, null, 'Bendamustine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bendamustine:</strong><br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin&nbsp;1.5-3 upper normal limit, or bilirubin &gt; 3 upper normal limit, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bendamustine:</strong><br>If CrCl &lt; 40 mL/min, do not use</li></ul>', 'Moderate', null, null, null, '<p><strong>Bendamustine:</strong> 120 mg/m2 IV days 1 and 2<br>Repeat every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (958, null, 'Pralatrexate', '<p>d</p>', 'Low', null, null, null, '<p><strong>Pralatrexate: </strong>30 mg/m2 IV weekly for 6 weeks<br>Repeat cycle every 7 weeks. Folic acid at 1-1.25 mg PO q day beginning<br>10 days prior to therapy and vitamin B12 at 1 mg IM beginning no more than 10<br>weeks prior to the first dose of therapy and should be repeated every 8-10 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (959, null, 'Brentuximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Brentuximab:</strong><br>If Child-Pugh A: Reduce dose to 1.2 mg/kg every 3 weeks.<br>If Child-Pugh B or C: Avoid use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Brentuximab:</strong><br>If CrCl ≥30 mL/min, no dose reduction &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/minute: Avoid use.</li></ul>', 'Low', null, null, null, '<p><strong>Brentuximab:</strong> 1.8 mg/kg IV on day 1<br>Repeat cycle every 3 weeks for up to 16 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (636, '', 'Capecitabine + Mitomycin-C + Radiation Therapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Mitomycin-C: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Mitomycin-C: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;10 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% dosage</li></ul>', 'Low', '', '', '', '<p><strong>Capecitabine:&nbsp;</strong>825 mg/m<sup>2</sup>&nbsp;PO twice daily on Monday-Friday</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (964, null, 'MPV', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Procarbazine:</strong><br>If AST, ALT are 1.6 to 6 times ULN: 75% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT are &nbsp;&gt;6 times ULN: Use clinical judgment &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;5 mg/dL or AST, ALT &gt;3 times ULN: Avoid use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Procarbazine:</strong><br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'High', null, null, null, '<p><strong>Methotrexate:</strong> 3,500 mg/m2 IV over 2 hours every other week for 5 doses</p><p><strong>Intrathecal methotrexate: </strong>12 mg IT weekly every other week after IV methotrexate<br><strong>Leucovorin: </strong>10 mg IV every 6 hours for 12 doses starting 24 hours after IV methotrexate; 10 mg IV every 12 hours for 8 doses starting 24 hours after IT methotrexate<br><strong>Vincristine:</strong> 1.4 mg/m2 IV every other week along with IV methotrexate<br><strong>Procarbazine:</strong> 100 mg/m2/day PO for 7 days on first, third, and fifth cycle of IV methotrexate.<br>Once chemotherapy is completed, whole-brain radiation therapy is administered<br>to a total dose of 45 cGy.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (965, null, 'R-MPV+ Radiation Therapy + Cytarabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Procarbazine:</strong><br>If AST, ALT are 1.6 to 6 times ULN: 75% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT are &nbsp;&gt;6 times ULN: Use clinical judgment &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;5 mg/dL or AST, ALT &gt;3 times ULN: Avoid use.</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Procarbazine:</strong><br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li></ul>', 'High', null, null, null, '<p><strong>Rituximab:</strong> 500 mg/m2 on day 1<br><strong>Methotrexate: </strong>3,500 mg/m2 IV on day 2<br><strong>Leucovorin: </strong>20-25 mg every 6 hours starting 24 hours after methotrexate infusion<br>for 72 hours or until serum methotrexate level, 1 x 10^-8 mg/dL.<br>Increase leucovorn to 40 mg every 4 hours IV, if methotrexate level &gt; 1 x 10^-8 mg/dL<br>at 48 hours or &gt;1 x 10^-8 mg/dL at 72 hours<br><strong>Vincristine:</strong> 1.4 mg/m2 (maximum, 2.8 mg) IV on day 2<br><strong>Procarbazine:</strong> 100 mg/m2 PO on days 1-7 of odd-numbered cycles only<br>If positive CSF cytology, administer 12 mg methotrexate IT between days 5 and 12<br>of each cycle<br>Repeat every 2 weeks for a total of 5 cycles.<br>After 5 cycles of R-MPV:<br>If CR, whole-brain radiotherapy (WBRT) 180 cGy/day for 13 days to a total of<br>2340 cGy beginning 3-5 weeks after the completion of R-MPV.<br>IF PR, administer 2 additional cycles of R-MPV. If CR is achieved after 7 cycles of<br>R-MPV, administer WBRT 180 cGy/day x 13 days to a total of 2,340 cGy beginning<br>3-5 weeks after the completion of R-MPV.<br>If persistent disease exists even after 7 cycles of R-MPV, administer WBRT<br>180 cGy/day x 25 days to a total of 4,500 cGy beginning 3-5 weeks after the<br>completion of R-MPV.<br>If stable or progressive disease after 5 cycles of R-MPV, then administer WBRT<br>180 cGy/day for 25 days to a total of 4,500 cGy beginning 3-5 weeks after the completion of R-MPV. Three weeks after the completion of WBRT, consolidation therapy is given with:&nbsp;</p><p>&nbsp;<strong>Cytarabine:</strong> 3 g/m2/day (maximum, 6 g) IV over 3 hours for 2 days.<br>The second cycle of cytarabine is given 1 month later.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (966, null, 'High-Dose Methotrexate', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Moderate', null, null, null, '<p><strong>Methotrexate:</strong> 8,000 mg/m2 IV on day 1<br>Repeat cycle every 2 weeks up to 8 cycles, followed by 8,000 mg/m2 IV on day 1 every month up to 100 months.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (967, null, 'Temozolomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Temozolomide:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild to moderate impairment (Child-Pugh classes A and B): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child-Pugh class C): There are no dosage adjustments provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Temozolomide:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥36 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;36 mL/min: There are no dosage adjustments provided by the manufacturer</li></ul>', 'Moderate', null, null, null, '<p><strong>Temozolomide:</strong> 150 mg/m2/day PO on days 1-5<br>Repeat cycle every 4 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (968, null, 'Topotecan', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Topotecan</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Topotecan</strong>:<br>If CrCl &gt; 40 mL/min, no dose reduction<br>If CrCl 20-39 mL/min, reduce to 0.75 mg/m2/dose</li></ul>', 'Low', null, null, null, '<p><strong>Topotecan:</strong> 1.5 mg/m2 IV on days 1-5<br>Repeat cycle every 3 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (724, '', 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Pembrolizumab:&nbsp;</strong>200 mg IV on day 1</p><p>Repeat cycle every 3 weeks. Consider this therapy only in the setting of MSI/dMMR tumors.</p>', 95);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (638, '', 'XELOX + Radiation Therapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: &nbsp;Reduce dose by 25%</li></ul>', 'Moderate', '', '', '', '<p><strong>Capecitabine:&nbsp;</strong>825 mg/m<sup>2</sup>&nbsp;PO twice daily on Monday-Friday for 6 weeks</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (721, '', 'Capecitabine + Oxaliplatin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Oxaliplatin:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Capecitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Reduce dose by 25%</li><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Capecitabine:&nbsp;</strong>1,000 mg/m<sup>2</sup>&nbsp;PO twice daily on days 1-14</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (379, 'Gemcitabine (Gemzar), CIsplatin (Platinum) ', 'Gemcitabine and Cisplatin (split dosing)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul>', 'High', '', 'Dash A, Pettus JA 4th, Herr HW, Bochner BH, Dalbagni G, Donat SM, Russo P, Boyle MG, Milowsky MI, Bajorin DF. A role for neoadjuvant gemcitabine plus cisplatin in muscle-invasive urothelial carcinoma of the bladder: a retrospective experience. Cancer. 2008 Nov 1;113(9):2471-7.', 'NEOADJUVANT', '<h4><strong>Cisplatin (Platinol):</strong>&nbsp;35 mg/m2&nbsp;IV once per day on days 1 and <strong>Gemcitabine (Gemzar):</strong>&nbsp;2,500 mg/m2&nbsp;IV on day 1.</h4><p>Repeat cycle every 14 days for 6 cycles.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (383, 'Gemcitabine (Gemzar), Paclitaxel (Taxol)', 'Gemcitabine and Paclitaxel (14 day cycle)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>No dose reduction</li></ul>', 'Moderate', '', 'Calabrò F, Lorusso V, Rosati G, Manzione L, Frassineti L, Sava T, Di Paula ED, Alonso S, Sternberg CN. Gemcitabine and paclitaxel every 2 weeks in patients with previously untreated urothelial carcinoma. Cancer. 2009 Jun 15;115(12):2652-9', 'METASTATIC', '<h4>&nbsp;</h4><p><strong>Paclitaxel (Taxol): </strong>150 mg/m2&nbsp;IV over 3 hours once on day 1,&nbsp;given first.</p><p><strong>Gemcitabine (Gemzar):&nbsp;</strong>2,500 mg/m2&nbsp;IV over 30 minutes once on day 1,&nbsp;given second.</p><p>&nbsp;</p><p>Repeat cycle every 14 days.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (384, 'Gemcitabine (Gemzar), Paclitaxel (Taxol)', 'Gemcitabine and Paclitaxel (21 day cycle)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>No dose reduction</li></ul>', 'Moderate', '', 'Meluch AA, Greco FA, Burris HA 3rd, O''Rourke T, Ortega G, Steis RG, Morrissey LH, Johnson V, Hainsworth JD. Paclitaxel and gemcitabine chemotherapy for advanced transitional-cell carcinoma of the urothelial tract: a phase II trial of the Minnie pearl cancer research network. J Clin Oncol. 2001 Jun 15;19(12):3018-24.', 'METASTATIC', '<h4>&nbsp;</h4><p><strong>Gemcitabine (Gemzar): </strong>1,000 mg/m2&nbsp;IV once per day on days 1, 8, 15.</p><p><strong>Paclitaxel (Taxol):</strong>&nbsp;200 mg/m2&nbsp;IV over 3 hours once on day 1.</p><p>Repeat cycle every 21-days.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (829, null, 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (total bilirubin &lt;1 to 1.5 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (total bilirubin &gt;1.5 times ULN): Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Pembrolizumab:&nbsp;</strong>200 mg&nbsp;IV on day 1.</p><p>Repeat cycles every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1116, null, 'Avelumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Avelumab:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Avelumab:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Avelumab:</strong> 10 mg/kg IV on day 1.</p><p>Repeat cycle every 2 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (295, 'Pomalidamide : Pomalyst', 'Pomalidomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pomalidomide:</strong><br>Avoid POMALYST in patients with serum bilirubin greater than 2.0 mg/dL and AST/ALT greater than 3.0 x ULN.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pomalidomide:</strong><br>Avoid Pomalidamide in patients with serum creatinine greater than 3.0 mg/dL.</li></ul>', 'Low', '<b>Pomalidamide</b>', 'Leleu X, et al. Pomalidomide plus low-dose dexamethasone is active and well tolerated in bortezomib and lenalidomide-refractory multiple myeloma: Intergroupe Francophone du Myélome 2009-02.$Blood. 2013 Mar 14;121(11):1968-75. doi: 10.1182/blood-2012-09-452375. Epub 2013 Jan 14$', null, '<p><strong>Pomalidomide: </strong>4 mg PO daily on days 1-21&nbsp;<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (289, 'Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'VAD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Moderate', '<b>VAD</b>', 'Segeren CM, Sonneveld P, Holt V, et al. Vincristine, doxorubicin and dexamethasone (VAD) administered as rapid intravenous infusion for first-line treatment in untreated multiple myeloma. $Br J Haematol. 1999;105(1):127-130.$', null, '<p><strong>Vincristine:</strong> 0.4 mg/day IV continuous infusion, days 1-4<br><strong>Doxorubicin:</strong> 9 mg/m2/day IV continuous infusion, days 1-4<br><strong>Dexamethasone:</strong> 40 mg/day PO, days 1-4, 9-12, 17-20<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (562, 'Carboplatin: Paraplatin<br>Paclitaxel: Taxol', 'Carboplatin/Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% of the usual dose. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% of the usual dose.</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Ozols RF. Combination regimens of paclitaxel and the platinum drugs as first-line regimens for ovarian cancer. Semin Oncol. 1995;22(6 Suppl 15):1-6. --- Pignata S, Breda E, et al. A phase II study of weekly carboplatin and paclitaxel as first-line treatment of elderly patients with advanced ovarian cancer. A Multicentre Italian Trial in Ovarian cancer (MITO-5) study. Crit Rev Oncol Hematol. 2008;66(3):229-36.', '', '<p><strong>Carboplatin:</strong>&nbsp;AUC of 6-7.5, IV on day 1<br><strong>Paclitaxel:</strong>&nbsp;175 mg/m2&nbsp;IV over 3 hours on day 1</p><p>Repeat cycle every 21 days.</p><p>OR</p><p><strong>Carboplatin:</strong>&nbsp;AUC of 2, IV on days 1, 8, and 15<br><strong>Paclitaxel:</strong>&nbsp;60 mg/m2&nbsp;IV on days 1, 8, and 15</p><p>Repeat cycle every 28 days.</p>', 99);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (388, 'Durvalumab (Imfinzi)', 'Durvalumab Monotherapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Durvalumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Durvalumab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction</li></ul>', 'Low', '', 'Study 1108: Massard C, Gordon MS, Sharma S, Rafii S, Wainberg ZA, Luke J, Curiel TJ, Colon-Otero G, Hamid O, Sanborn RE, O''Donnell PH, Drakaki A, Tan W, Kurland JF, Rebelatto MC, Jin X, Blake-Haskins JA, Gupta A, Segal NH. Safety and efficacy of durvalumab (MEDI4736), an anti-programmed cell death ligand-1 immune checkpoint inhibitor, in patients with advanced urothelial bladder cancer. J Clin Oncol. 2016 Sep 10;34(26):3119-25. Epub 2016 Jun 6.', 'METASTATIC', '<h4>&nbsp;</h4><p>&nbsp; <strong>Durvalumab: </strong>10 mg/kg IV over 60 minutes once on day 1</p><p>Repeat cycle every 14 days for up to 26 cycles (1 year). Patients could receive another 12 months of treatment if they experienced disease progression, had not received other anticancer treatment and did not meet the criteria for discontinuation.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (393, 'Enfortumab vedotin (Padcev) ', 'Enfortumab vedotin monotherapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Enfortumab vedotin (Padcev):</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Enfortumab vedotin (Padcev):</strong><br>No dose reduction</li></ul>', 'Moderate', '', 'Rosenberg JE, O''Donnell PH, Balar AV, McGregor BA, Heath EI, Yu EY, Galsky MD, Hahn NM, Gartner EM, Pinelli JM, Liang SY, Melhem-Bertrandt A, Petrylak DP. Pivotal Trial of Enfortumab Vedotin in Urothelial Carcinoma After Platinum and Anti-Programmed Death 1/Programmed Death Ligand 1 Therapy. J Clin Oncol. 2019 Oct 10;37(29): 2592-2600. Epub 2019 Jul 29. ', 'METASTATIC', '<h4>&nbsp;</h4><p><strong>Enfortumab vedotin (Padcev):</strong>&nbsp;1.25 mg/kg (maximum dose of 125 mg) IV over 30 minutes once per day on days 1, 8, 15</p><p>Repeat cycle every 28 days.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (969, null, 'Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Gemcitabine:</strong> 1,200 mg/m2 IV on days 1,8, and 15.<br>Repeat cycle every 28 days.<br>&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (970, null, 'Pemetrexed', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pemetrexed</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pemetrexed</strong>:<br>If CrCl &gt; 45 mL/min, no dose reduction<br>If CrCl &lt; 45 mL/min, do not use</li></ul>', 'Low', null, null, null, '<p><strong>Pemetrexed: </strong>500 mg/m2 IV on day 1<br>Repeat cycle every 21 days. Folic acid at 350-1,000 ug PO daily beginning<br>1-2 weeks prior to therapy and vitamin B12 at 1,000 ug IM to start 1-2 weeks prior<br>to the first dose of therapy and repeated every 3 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (972, null, 'CMV', '<p><strong>Hepatic Impairment</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><ul><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Vinblastine:</strong><br>If serum bilirubin 1.5 to 3 mg/dL or transaminases 2 to 3 times ULN: Administer 50% of dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If serum bilirubin &gt;3 times ULN: Avoid use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug.</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Vinblastine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin:</strong> 100 mg/m2 IV on day 2 (give 12 hours after methotrexate)<br><strong>Methotrexate:</strong> 30 mg/m2 IV on days 1 and 8<br><strong>Vinblastine:</strong> 4 mg/m2 V on days 1 and 8<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (973, null, 'MCV', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use.</li><li><strong>Vinblastine:</strong><br>If serum bilirubin 1.5 to 3 mg/dL or transaminases 2 to 3 times ULN: Administer 50% of dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If serum bilirubin &gt;3 times ULN: Avoid use.</li><li><strong>Carboplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Vinblastine:</strong><br>No dose reduction</li><li><strong>Carboplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% dosage.</li></ul>', 'High', null, null, null, '<p><strong>Methotrexate: </strong>30 mg/m2 IV on days 1, 15, and 22<br><strong>Carboplatin:</strong> AUC of 4.5, IV on day 1<br><strong>Vinblastine:</strong> 3 mg/m2 IV on days 1, 15, and 22<br>Repeat cycle every 28 days.</p>', null);
