insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1117, null, 'CAV', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Cyclophosphamide:</strong> 1,000 mg/m2 IV on day 1<br><strong>Doxorubicin:</strong> 40 mg/m2 IV on day 1<br><strong>Vincristine:</strong> 1mg/m2 IV on day 1 (maximum, 2 mg)<br>Repeat cycle every 21 days&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (670, 'Temodar', 'Temozolamide + RT (Glioblastoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Temozolomide:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Temozolomide:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul>', 'Moderate', '', '', 'ADJUVANT', '<p><strong>Radiation therapy:</strong> 200 cGy/day for 5 days per week for 6 weeks.</p><p><strong>Temozolomide: </strong>75 mg/m2 PO daily for 6 weeks with radiation therapy. After a 4 weeks break, 150 mg/m2 PO on days 1-5.</p><p>Repeat temozolomide monotherapy every 28 days for 6 cycles. If the patient tolerated well can increase the dose to 200 mg/m2 on subsequent cycles.</p><p><strong>Supportive medications</strong></p><ul><li>PCP prophylaxis with one of the following:<ul><li>Trimethoprim/sulfamethoxazole</li><li>Pentamidine 300 mg nebulized inhaled</li></ul></li><li>Metoclopramide( Reglan) or 5 HT-3 antagonist recommended before the initial doses of radiation therapy &amp; temozolomide</li></ul><h4>&nbsp;</h4>', 1);
