insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (114, 'Cyclophosphamide: Cytoxan<br>Docetaxel: Taxotere<br>Doxorubicin: Adriamycin, various', 'AC/Docetaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp;SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline phosphatase &gt; 2.5 upper normal limit, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Docetaxel:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li></ul>', 'High', '<b>AC/Docetaxel</b>', 'Swain SM, Jeong JH, et al. Longer therapy, iatrogenic amenorrhea, and survival in early breast cancer. N Engl J Med. 2010;362(22):2053-2065.', null, '<p><strong>Doxorubicin:</strong> 60 mg/m2 IV, day 1<br><strong>Cyclophosphamide:</strong> 600 mg/m2 IV, day 1<br>Repeat every 21 days for 4 cycles, followed by<br><strong>Docetaxel:</strong> 100 mg/m2 IV, day 1<br>Repeat every 21 days for 4 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (402, 'Cyclophosphamide: Cytoxan<br>Doxorubicin: Adriamycin, various<br>Paclitaxel: Taxol', 'AC/Paclitaxel (weekly) ', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide</strong>:<br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>No dose reduction</li></ul>', 'High', '', 'Sparano JA, Wang M, et al. Weekly paclitaxel in the adjuvant treatment of breast cancer. N Engl J Med. 2008;358(16):1663-71.', 'ADJUVANT', '<p><strong>Doxorubicin</strong>: 60 mg/m2&nbsp;IV on day 1<br><strong>Cyclophosphamide</strong>: 600 mg/m2&nbsp;IV on day 1</p><p>Repeat every 21 days for 4 cycles, followed by</p><p><strong>Paclitaxel</strong>: 80 mg/m2&nbsp;IV on day 1</p><p>Repeat cycle every 7&nbsp;days for 12&nbsp;cycles.</p>', 15);
