insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (122, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Fluorouracil : 5-FU', 'CAF(oral)', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>CAF(oral)</b>', 'Hutchins LF, Green SJ, Favolin PM, et al. Randomized controlled trial of cyclophosphamide, methotrexate, and fluorouracil versus cyclophosphamide, doxorubicin, and fluorouracil with and without tamoxifen for high risk, node-negative breast cancer:treatment results of Intergroup Protocol INT-0102. $J Clin Oncol. 2005;23(33):8313-8321.$', null, '<p><strong>Cyclophosphamide:</strong> 100 mg/m2 PO, days 1-14<br><strong>Doxorubicin:</strong> 30 mg/m2 IV ,days 1, and 8<br><strong>Fluorouracil:</strong> 500 mg/m2 IV, days 1, and 8<br>Repeat every 28 days for 6 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (452, 'Carboplatin: Paraplatin<br>Docetaxel: Taxotere', 'Carboplatin/Docetaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Docetaxel</strong>:<br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline phosphatase &gt; 2.5 upper normal limit, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on AUC &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% dosage.</li><li><strong>Docetaxel</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Fitch V, et al. Proc Am Soc Clin Oncol 2003;22:23 (abstract 90).', 'METASTATIC', '<p><strong>Carboplatin:</strong>&nbsp;AUC of 6, IV on day 1<br><strong>Docetaxel:</strong>&nbsp;75 mg/m2&nbsp;IV on day 1</p><p>Repeat cycle every 21 days.</p>', 15);
