insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (132, 'Cyclophosphamide : Cytoxan<br>Docetaxel : Taxotere', 'TC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline Phosphatase &gt;2.5 upper normal limit, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Docetaxel:</strong><br>No dose reduction</li></ul>', 'High', '<b>TC</b>', 'Jones SE, Savin MA, Holmes FA, et al. Phase III trial comparing doxorubicin plus cyclophosphamide with docetaxel plus cyclophosphamide as adjuvant therapy for operable breast cancer. $J Clin Oncol. 2006:24(34):5381-5387.$', null, '<p><strong>Docetaxel:</strong> 75 mg/m2 IV over 30-60 minutes, day 1<br><strong>Cyclophosphamide:</strong> 600 mg/m2 IV over 30-60 minutes, day 1<br>Repeat every 21 days for 4 cycles</p>', null);
