insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (257, 'Cytarabine : Ara-C<br>Idarubicin : Idamycin<br>Mitoxantrone : Novantrone', '7 + 3', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>If bilurubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Idarubicin:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 25%<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Mitoxantrone:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 25%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Idarubicin:</strong><br>If CrCl &lt; 10 mL/min, reduce dose by 25%</li><li><strong>Mitoxantrone:</strong><br>No dose reduction</li></ul>', 'Moderate', '<b>7 + 3</b>', 'Presler H, Davis RB, Kirshener J, et al. Comparison of three remission induction regimens and two post-induction strategies for the treatment of acute nonlymphocytic leukemia: a Cancer and Leukemia Group B study. $Blood. 1987;69(5):1441-1449.$<br> Wiernik PH, BAnks PL, Case DC Jr, et al. Cytarabine plus idarubicin or daunorubicin as induction and consolidation therapy for previously untreated patients with acute myeloid leukemia. $Blood. 1992;79(2):313-319.$<br> Wahlin A, Hornsten P, Hedenus M, Malm C. Mitoxantrone and cytarabine versus daunorubicin and cytarabine in previously untreated patients with acute myeloid leukemia. $Cancer Chemother Pharmacol. 1991;28(6):480-483.$', null, '<p><strong>Cytarabine:</strong> 100 mg/m2/day IV continuous infusion on days 1-7<br><strong>Daunorubicin:</strong> 45 mg/m2 IV on days 1-3<br><strong>OR</strong><br><strong>Idarubicin:</strong> 13 mg/m2/day IV, days 1-3<br><strong>OR</strong><br><strong>Mitoxantrone:</strong> 12 mg/m2/day &nbsp;IV, days 1-3</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (258, 'Cytarabine : Ara-C', '7 + 3 with hi-dose Daunorubicin (<60 years)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>If bilurubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li></ul>', 'Moderate', '<b>7 + 3 with hi-dose daunorubicin <br>(<60 years)</b>', 'Fernandez HF, Sun Z, Pao X, et al. Anthracycline dose intensification in acute myeloid leukemia. $N Engl J Med. 2009;361(13):1249-1259.$', null, '<p><strong>Cytarabine:</strong> 100 mg/m2/day CIVI, days 1-7<br><strong>Daunorubicin:</strong> 90 mg/m2/day IV, days 1-3</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (672, '', 'Ivosidenib (AML IDH1 mutated) ', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ivosidenib:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ivosidenib:</strong><br>No dose reduction</li></ul>', 'Low', '', '', 'FIRST LINE ', '<h4><strong>Ivosidenib:</strong> 500 mg PO once per day</h4><p>Continue for at least 6 months ( to allow time for clinical response) and then until disease progression or unacceptable toxicity occurs.</p>', 33);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (294, 'Carfilzomib : Kyprolis', 'Carfilzomib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carfilzomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If total&nbsp;bilirubin&nbsp;1-3 times upper normal limit: Reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If total bilirubin&nbsp;&gt;3 times upper normal limit: No dosage recommendation. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carfilzomib:</strong><br>No dose reduction.</li></ul>', 'Low', '<b>Carfilzomib</b>', 'Siegel DS, et al. A phase 2 study of single-agent carfilzomib (PX-171-003-A1) in patients with relapsed and refractory multiple myeloma.$Blood. 2012 Oct 4;120(14):2817-25. doi: 10.1182/blood-2012-05-425934. Epub 2012 Jul 25$', null, '<p><strong>Carfilzomib:</strong> 20 mg/m2 IV on days 1,2,8,9,15, and 16. After cycle 1, increase dose to 27 mg/m2 IV.<br>&nbsp;</p><p>Repeat cycle every 28 days for up to 12 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1020, null, 'Nivolumab + Ipilimumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ipilimumab:</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li><li><strong>Nivolumab:</strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ipilimumab:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Nivolumab:</strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Ipilimumab:</strong> 1 mg/kg IV on day 1<br><strong>Nivolumab:</strong> 1 mg/kg IV on day 1<br>Repeat cycle every 21 days for 4 cycles followed by<br><strong>Nivolumab:</strong> 240 mg IV on day 1<br>Repeat cycle every 14 days until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1018, null, 'Cobimetinib + Vemurafenib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cobimetinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li><li><strong>Vemurafenib:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild to moderate impairment (Child-Pugh classes A and B): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (Child-Pugh class C): Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cobimetinib:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Dose modification not provided by the manufacturer</li><li><strong>Vemurafenib:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If mild to moderate impairment (preexisting): 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If Severe impairment (preexisting): Dose modification not provided by the manufacturer.</li></ul>', 'Low', null, null, null, '<p><strong>Cobimetinib: </strong>60 mg/day PO on days 1-21<br><strong>Vemurafenib: </strong>960 mg PO bid daily<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (255, 'Idarubicin : Idamycin', 'ATRA/Idarubicin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li><li><strong>Idarubicin:</strong>&nbsp;<br>If bilirubin 2.6-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li><li><strong>Idarubicin:</strong><br>If CrCl &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 10 to 50 mL/minute: reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: &nbsp;reduce dose by 50%</li></ul>', 'Moderate', '<b>ATRA/Idarubicin</b>', 'Sanz MA, Montesinos P, Vellenga E, et al. Risk-adapted treatment of acute promyelocytic leukemia with all-trans retinoic acid and anthracycline monochemotherapy: long-term outcomeon the LPA99 multicenter study by the PETHEMA Group. $Blood. 2008;112(8):3130-3134.$', null, '<p><strong>All-trans-retinoic acid</strong> (ATRA): 45 mg/m2 PO, daily.<br><strong>Idarubicin:</strong> 12 mg/m2 IV, on days 2, 4, 6, and 8.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (981, null, 'FLAG', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Fludarabine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Fludarabine:</strong><br>If CrCl 10-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt;10 mL/min: Reduce dose by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Fludarabine:</strong> 30 mg/m2 IV on days 1-5<br><strong>Cytarabine:</strong> 2,000 mg/m2/day IV over 4 hours on days 1-5 starting 3.5 hours after<br>fludarabine<br><strong>G-CSF: </strong>5 µg/kg/day SC starting 24 hours before chemotherapy.<br>An additional cycle may be given in the setting of a partial response.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (982, null, 'Ara-C + Daunorubicin (5 + 2)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li></ul>', 'Moderate', null, null, null, '<p><strong>Cytarabine:</strong> 100 mg/m2/day IV continuous infusion on days 1-5<br><strong>Daunorubicin:</strong> 45 mg/m2 IV on days 1 and 2</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (983, null, 'Liposomal Daunorubicin and Cytarabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Liposomal Daunorubicin:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Liposomal Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Liposomal Daunorubicin and Cytarabine:</strong> 29 mg/m2 daunorubicin and 65 mg/m2<br>cytarabine on days 1 and 3</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (822, null, ' Ara-C+ Idarubicin (Induction Regimen)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Idarubicin:</strong>&nbsp;<br>If bilirubin 2.6-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Idarubicin:</strong>&nbsp;<br>If CrCl &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 10 to 50 mL/minute: reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: &nbsp;reduce dose by 50%</li></ul>', 'Moderate', null, null, null, '<p><strong>Cytarabine:</strong> 100 mg/m2/day IV continuous infusion on days 1-7<br><strong>Idarubicin:</strong> 12 mg/m2 IV on days 1-3.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (978, null, 'Tretinoin + Daunorubicin + Cytarabine (Acute promyelocytic leukemia)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li></ul>', 'Moderate', null, null, null, '<p><strong>ATRA:</strong> 45 mg/m2 PO daily<br><strong>Daunorubicin:</strong> 60 mg/m2 IV on days 1-3<br><strong>Cytarabine:</strong> 200 mg/m2 IV on days 1-7</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (979, null, 'Tretinoin + Arsenic trioxide (Acute promyelocytic leukemia)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Arsenic trioxide:</strong>&nbsp;<br>No dose reduction</li><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Arsenic trioxide:</strong>&nbsp;<br>If CrCl ≥30 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Use with caution, may require dosage reduction (monitor closely for toxicity)</li><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li></ul>', 'Low', null, null, null, '<p><strong>ATRA:</strong> 45 mg/m2 PO daily<br><strong>Arsenic trioxide:</strong> 0.15 mg/kg/day IV starting on day 10<br>Continue treatment until CR. Once in CR, patients should receive the following:<br><strong>ATRA:</strong> 45 mg/m2 PO daily on weeks 1-2, 5-6, 9-10, 13-14, 17-18, 21-22, and 25-26<br><strong>Arsenic trioxide:</strong> 0.15 mg/kg/day IV on Monday-Friday on weeks 1-4, 9-12, 17-20, and 25-28<br>Therapy should be terminated 28 weeks after the CR date.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (980, null, 'Mitoxantrone + Etoposide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Mitoxantrone:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 25%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Mitoxantrone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Mitoxantrone: </strong>10 mg/m2/day IV on days 1-5<br><strong>Etoposide:</strong> 100 mg/m2/day IV on days 1-5</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (984, null, 'Ara-C + Idarubicin (Consolidation Regimen)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Idarubicin:</strong>&nbsp;<br>If bilirubin 2.6-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Idarubicin:</strong>&nbsp;<br>If CrCl &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 10 to 50 mL/minute: reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: &nbsp;reduce dose by 50%</li></ul>', 'Moderate', null, null, null, '<p><strong>Cytarabine:</strong> 100 mg/m2 IV continuous infusion on days 1-5<br><strong>Idarubicin:</strong> 13 mg/m2 IV on days 1 and 2<br>Repeat cycle every 21-28 days.<br>&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (992, null, 'Gilteritinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gilteritinib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided because of insufficient data. &nbsp;&nbsp;<strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gilteritinib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided because of insufficient data.</li></ul>', 'Low', null, null, null, '<p><strong>Gilteritinib:</strong> 120 mg PO daily<br>Repeat cycle every 28 days.</p>', null);
