insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (990, null, 'Azacitidine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Azacitidine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided because of insufficient data. &nbsp;&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Azacitidine:</strong><br>No dose reduction.</li></ul>', 'Moderate', null, null, null, '<p><strong>Azacitidine: </strong>75 mg/m2 SC daily for 7 days<br>Repeat the cycle every 28 days for at least 6 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (991, null, 'Decitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Decitabine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided because of insufficient data. &nbsp;&nbsp;<strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Decitabine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided because of insufficient data.&nbsp;</li></ul>', 'Low', null, null, null, '<p><strong>Decitabine:</strong> 20 mg/m2 IV on days 1-5<br>Repeat cycle every 4 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (221, 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'ABVD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>No dose reduction</li><li><strong>Dacarbazine:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vinblastine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Dacarbazine:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vinblastine:</strong><br>No dose reduction</li></ul>', 'High', '<b>ABVD</b>', 'Harker WG, Kushlan P, Rosenberg SA. Combination chemotherapy for advanced Hodgkin''s disease after failure of MOPP: ABVD and B-CAVe. $Ann Intern Med. 1984;  101(4):440-446$.<br> Longo DL, Glastein E, Duffey PL,et al. Alternating MOPP and ABVD chemotherapy plus mantle-field radiation therapy in patients with massive mediastinal Hodgikin''s disease. $J Clin Oncol. 1997; 15(11): 3338-3346$.     ', null, '<p><strong>Doxorubicin:</strong> 25 mg/m2 IV on days 1 and 15<br><strong>Bleomycin:</strong> 10 U/m2 IV on days 1 and 15<br><strong>Vinblastine:</strong> 6 mg/m2 IV on &nbsp;days 1 and 15<br><strong>Dacarbazine:</strong> 375 mg/m2 IV on days 1 and 15<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (993, null, 'A + AVD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Brentuximab:</strong><br>If mild impairment (Child-Pugh class A): Initial dose 0.9 mg/kg (maximum dose: 90 mg) every 2 weeks. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If moderate to severe impairment (Child-Pugh class B or C): Avoid use.</li><li><strong>Dacarbazine:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vinblastine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Brentuximab:</strong><br>If CrCl ≥30 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/minute: Avoid use.</li><li><strong>Dacarbazine:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vinblastine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Brentuximab:</strong> 1.2 mg/kg IV on days 1 and 15<br><strong>Doxorubicin: </strong>25 mg/m2 IV on days 1 and 15<br><strong>Vinblastine: </strong>6 mg/m2 IV on days 1 and 15<br><strong>Dacarbazine:</strong> 375 mg/m2 IV on days 1 and 15</p><p>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (994, null, 'MOPP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Nitrogen mustard: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Procarbazine:</strong><br>If AST, ALT are 1.6 to 6 times ULN: 75% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT are &nbsp;&gt;6 times ULN: Use clinical judgment &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;5 mg/dL or AST, ALT &gt;3 times ULN: Avoid use.</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Nitrogen mustard: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Procarbazine:</strong><br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li></ul>', 'High', null, null, null, '<p><strong>Nitrogen mustard:</strong> 6 mg/m2 IV on days 1 and 8<br><strong>Vincristine:</strong> 1.4 mg/m2 IV on days 1 and 8<br><strong>Procarbazine:</strong> 100 mg/m2 PO on days 1-14<br><strong>Prednisone:</strong> 40 mg/m2 PO on days 1-14<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (290, 'Bortezomib : Velcade<br>Melphalan : Alkeran<br>Prednisone : various', 'BMP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin&nbsp;1-1.5 times upper normal limit: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin&nbsp;&gt;1.5 times upper normal limit: Reduce the initial dose to 0.7 mg/m2.</li><li><strong>Melphalan:</strong><br>No dose reduction</li><li><strong>Prednisone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li><li><strong>Melphalan:</strong><br>&nbsp;If CrCl 10 to 50 mL/min: Reduce dose to 75%. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose to 50%.</li><li><strong>Prednisone:</strong><br>No dose reduction</li></ul>', 'Low', '<b>VMP</b>', 'San Miguel JF, Schalg R, Khuageva NK, et al. MMY-3002 a phase 3 study comparing bortezomib-melphalan-prednisone (VMP) with melphalan-prednisone (MP) in newly diagnosed multiple myeloma. $Blood. 2007;110(11 pt 1): Abstract 78.$<br> San Miguel JF, Schlag R, Khuageva NK, et al. Bortezomib plus melphalan and prednisone for initial treatment of multiple myeloma. $N Engl J Med. 2008;359(9):906-917.$', null, '<p><strong>Bortezomib:</strong> 1.3 mg/m2 IV or SC on days 1, 4, 8,11, 22, 25, 29, and 32<br><strong>Melphalan:</strong> 9 mg/m² PO on days 1-4<br><strong>Prednisone: </strong>60 mg/m2 PO on days 1-4<br>Repeat cycle every 6 weeks for 4 cycles, followed by<br><strong>Bortezomib:</strong> 1.3 mg/m2 IV on days 1, 8, 22, and 29<br><strong>Melphalan: </strong>9 mg/m2 PO on days 1-4<br><strong>Prednisone:</strong> 60 mg/m2 PO on days 1-4<br>Repeat cycle every 6 weeks for 5 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1121, null, 'Melphalan/Prednisone/Lenalidomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Melphalan:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Lenalidomide:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Melphalan:</strong><br>&nbsp;If CrCl 10 to 50 mL/min: Reduce dose to 75%. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose to 50%.</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li></ul>', 'Low', null, null, null, '<p><strong>Melphalan:</strong> 0.18 mg/kg PO, days 1-4<br><strong>Prednisone:</strong> 2 mg/kg PO, days 1-4<br><strong>Lenalidomide: </strong>10 mg/day PO on days 1 to 21</p><p>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (995, null, 'MOPP/ABVD Hybrid', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Nitrogen mustard: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Procarbazine:</strong><br>If AST, ALT are 1.6 to 6 times ULN: 75% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT are &nbsp;&gt;6 times ULN: Use clinical judgment &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;5 mg/dL or AST, ALT &gt;3 times ULN: Avoid use.</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Bleomycin:</strong><br>No dose reduction</li><li><strong>Hydrocortisone:</strong>&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Vinblastine:</strong><br>If serum bilirubin 1.5 to 3 mg/dL or transaminases 2 to 3 times ULN: Administer 50% of dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If serum bilirubin &gt;3 times ULN: Avoid use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Nitrogen mustard: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Procarbazine:</strong><br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Bleomycin:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Hydrocortisone:</strong>&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li><li><strong>Vinblastine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Nitrogen mustard:</strong> 6 mg/m2 IV on days 1 and 8<br><strong>Vincristine:</strong> 1.4 mg/m2 IV on day 1 (maximum dose, 2 mg)<br><strong>Procarbazine:</strong> 100 mg/m2 PO on days 1-14<br><strong>Prednisone:</strong> 40 mg/m2 PO on days 1-14<br><strong>Doxorubicin:</strong> 35 mg/m2 IV on day 8<br><strong>Bleomycin: </strong>10 U/m2 IV on day 8<br><strong>Hydrocortisone:</strong> 100 mg IV given before bleomycin<br><strong>Vinblastine:</strong> 6 mg/m2 IV on day 8<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1118, null, 'Imatinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Imatinib</strong>:<br>&nbsp;If bilirubin &lt; 3 upper normal limit, no dose reduction<br>If bilirubin &gt; 3 upper normal limit, use with caution</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Imatinib</strong>:<br>If CrCl 40 to 59 mL/min: Maximum dose recommended-600 mg. &nbsp; If CrCl 20 to 39 mL/min: Reduce dose by 50%; dose may be increased as tolerated; maximum recommended dose: 400 mg. &nbsp; If CrCl &lt;20 mL/minute: Use caution; 100 mg daily dose has been tolerated in a limited number of patients with severe impairment.</li></ul>', 'High', null, null, null, '<p><strong>Imatinib: </strong>400 mg PO daily<br><br>Continue until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1119, null, 'Antithymocyte globulin (ATG) + Cyclosporine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>ATG:&nbsp;</strong><br>Dose modification not provided by the manufacturer</li><li><strong>Cyclosporine:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>ATG:&nbsp;</strong><br>Dose modification not provided by the manufacturer</li><li><strong>Cyclosporine:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>ATG: </strong>15 mg/kg IV on days 1-5<br><strong>Cyclosporine: </strong>5-6 mg/kg PO bid<br>Adjust cyclosporine dose to maintain blood levels between 100 and 300 ng/mL</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1120, null, 'Cemiplimab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cemiplimab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild to moderate impairment (Child Pugh class A or B): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child Pugh class C): There are no dosage adjustments provided in the manufacturer''s labeling</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cemiplimab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥21 mL/min: No dosage adjustment necessary. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;21 mL/min: There are no dosage adjustments provided in the manufacturer''s labeling</li></ul>', 'Low', null, null, null, '<p><strong>Cemiplimab: </strong>350 mg once every 3 weeks.</p><p>Continue treatment until disease progression or unacceptable toxicity. Note- Select the patients for treatment based on PD-L1 expression (on ≥50% of tumor cells).</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1122, null, 'Thalidomide + Dexamethasone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Thalidomide:</strong> 200 mg/day PO<br><strong>Dexamethasone:</strong> 40 mg/day PO on days 1-4,9-12, and 17-20 for first 4 cycles and then 40 mg/day PO on days 1-4<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1123, null, 'Lenalidomide + Dexamethasone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>No dose reduction.</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Lenalidomide: </strong>25 mg/day PO on days 1-21<br><strong>Dexamethasone:</strong> 40 mg/day PO on days 1-4,9-12, and 17-20 (first 4 cycles) and then 40 mg/day PO on days 1-4 with future cycles<br>Repeat cycles every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1124, null, 'Panobinostat + Bortezomib + Dexamethasone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin&nbsp;1-1.5 times upper normal limit: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin&nbsp;&gt;1.5 times upper normal limit: Reduce the initial dose to 0.7 mg/m2.</li><li><strong>Panobinostat:</strong><br>If mild impairment (AST &gt; ULN and total bilirubin ≤ ULN <strong>or</strong> total bilirubin &gt;1 to ≤1.5 times ULN and any AST): Reduce initial dose to 15 mg; monitor frequently for adverse events and adjust dose as needed for toxicity. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate impairment (total bilirubin &gt;1.5 to ≤3 times ULN and any AST):Reduce initial dose to 10 mg; monitor frequently for adverse events and adjust dose as needed for toxicity. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (total bilirubin &gt;3 times ULN and any AST): Avoid use.</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li><li><strong>Panobinostat:</strong><br>There are no dosage adjustments provided in the manufacturer''s labeling</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Panobinostat:</strong> 20 mg/day PO on days 1,3,5, 8, 10, and 12<br><strong>Bortezomib: </strong>1.3 mg/m2 IV on days 1, 4, 8, and 11<br><strong>Dexamethasone: </strong>20 mg/day PO on days 1, 2, 4, 5, 8,9, 11, and 12<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1000, null, 'GVD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT elevated (with normal bilirubin): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;1.6 mg/dL: Reduce initial dose to 800 mg/m2</li><li><strong>Vinorelbine</strong>:<br>If bilirubin 2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Doxil:</strong><br>If bilirubin ≥1.2 mg/dL: Consider decreasing the dose of the drug.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li><li><strong>Vinorelbine</strong>:<br>No dose reduction</li><li><strong>Doxil:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p>For transplant-naïve patients:<br><strong>Gemcitabine:</strong> 1,000 mg/m2 IV on days 1 and 8<br><strong>Vinorelbine:</strong> 20 mg/m2 IV on days 1 and 8<br><strong>Doxil:</strong> 15 mg/m2 IV on days 1 and 8<br>Repeat cycle every 21 days.<br>&nbsp;</p><p><strong>OR</strong></p><p><br>For post-transplant patients:<br><strong>Gemcitabine:</strong> 800 mg/m2 IV on days 1 and 8<br><strong>Vinorelbine:</strong> 15 mg/m2 IV on days 1 and 8<br><strong>Doxil:</strong> 10 mg/m2 IV on days 1 and 8<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1001, null, 'Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT elevated (with normal bilirubin): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;1.6 mg/dL: Reduce initial dose to 800 mg/m2</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Gemcitabine:</strong> 1,250 mg/m2 IV on days 1,8, and 15<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1002, null, 'Rituximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Rituximab:</strong> 375 mg/m2 IV on day 1<br>Repeat weekly for 4 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1003, null, 'Brentuximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Brentuximab:</strong><br>If mild impairment (Child-Pugh class A): Initial dose 0.9 mg/kg (maximum dose: 90 mg) every 2 weeks. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If moderate to severe impairment (Child-Pugh class B or C): Avoid use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Brentuximab:</strong><br>If CrCl ≥30 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/minute: Avoid use.</li></ul>', 'Low', null, null, null, '<p><strong>Brentuximab:</strong> 1.8 mg/kg IV on day 1<br>Repeat cycle every 3 weeks for up to 16 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1004, null, 'Nivolumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Nivolumab:</strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Nivolumab:</strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Nivolumab:</strong> 240 mg IV on day 1<br>Repeat cycle every 2 weeks. May also administer 480 mg IV with cycles repeated every 4 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1005, null, 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment: No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment: Dosage adjustment not provided by the manufacturer.</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Pembrolizumab:</strong> 200 mg IV on day 1<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (145, 'Letrozole : Femara', 'Letrozole', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Letrozole:</strong><br>If Child-Pugh A or B, no dose reduction&nbsp;<br>If Child-Pugh C, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Letrozole:</strong><br>If CrCl &gt; 10 mL/min, no dose reduction</li></ul>', 'NA', '<b>Letrozole</b>', 'Dombernowsky P, Smith I, Falkson G, et al. Letrozole, a new oral aromatase inhibitor for advanced breast cancer: double-blind randomized trial showing a dose effect and improved efficacy and tolerability compared with megestrol aectate. $J Clin Oncol. 1998;16(2):453-461.$<br> Ingle JN, et al. A randomized phase II trial of two dosage levels of letrozole as third-line hormonal therapy for women with metastatic breast carcinoma. $Cancer. 1997;80(2):218-224.$<br> Goss PE, Ingle JN, Martino S, et al. A randomized trial of letrozole in postmenopausal women after five years of tamoxifen therapy for early-stage breast cancer. $N Engl J Med. 2003;349(19): 1793-1802.$', null, '<p><strong>Letrozole:</strong> 2.5 mg PO once daily</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1007, null, 'Everolimus', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Everolimus:</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (Child-Pugh class A): Reduce dose to 7.5 mg once daily &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate impairment (Child-Pugh class B): Reduce dose to 5 mg once daily &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (Child-Pugh class C): Reduce dose to 2.5 mg once daily</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Everolimus:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Everolimus:</strong> 10 mg PO daily<br>Continue until disease progression</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1008, null, 'Lenalidomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li></ul>', 'Low', null, null, null, '<p><strong>Lenalidomide: </strong>25 mg PO daily<br>Continue until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1009, null, 'Mogamulizumab-kpkc', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Mogamulizumab-kpkc:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild to moderate impairment (total bilirubin &lt; 3 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (total bilirubin &gt; 3 times ULN): Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Mogamulizumab-kpkc:</strong><br>No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Mogamulizumab-kpkc:</strong> 1 mg/kg IV on days 1, 8, 15, and 22 of cycle 1 and then on days 1 and 15 of all subsequent cycles<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1016, null, 'Temozolomide + Thalidomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Temozolomide:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild to moderate impairment (Child-Pugh classes A and B): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child-Pugh class C): There are no dosage adjustments provided by the manufacturer.</li><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Temozolomide:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥36 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;36 mL/min: There are no dosage adjustments provided by the manufacturer</li><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Temozolomide:</strong> 75 mg/m2/day PO for 6 weeks<br><strong>Thalidomide:</strong> 200 mg/day PO for 6 weeks<br>Repeat cycle every 8 weeks. Consider dose escalation to 400 mg/day for patients &lt;70 years and starting at a lower dose of 100 mg/day with dose escalation to 250 mg/day for patients &gt;70 years.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1019, null, 'Binimetinib + Encorafenib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Binimetinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild impairment (Child-Pugh classes A): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (Child-Pugh class B and C): Reduce dose to 30 mg PO bid.</li><li><strong>Encorafenib:</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild impairment (Child-Pugh classes A): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (Child-Pugh class B and C): Dose modification not provided by the manufacturer &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Binimetinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li><li><strong>Encorafenib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If CrCl ≥30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Dose modification not provided by the manufacturer &nbsp;</li></ul>', 'High', null, null, null, '<p><strong>Binimetinib:</strong> 45 mg PO bid<br><strong>Encorafenib:</strong> 300 mg PO daily<br>Continue treatment until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (963, null, 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (total bilirubin &gt;1 to 1.5 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (total bilirubin &gt;1.5 times ULN): Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Pembrolizumab:</strong> 200 mg IV on day 1<br>Repeat cycle every 21 days.<br>&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1010, null, 'Vorinostat', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Vorinostat: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild to moderate impairment (total bilirubin &lt; 3 times ULN): Reduce dose to 300 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (total bilirubin &gt; 3 times ULN): Dosage adjustment not provided by the manufacturer <strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Vorinostat:</strong><br>No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Vorinostat: </strong>400 mg PO daily<br>Continue therapy until disease progression</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1011, null, 'Romidepsin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Romidepsin: &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild impairment (total bilirubin &gt;1 to 1.5 times ULN): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate impairment (total bilirubin &gt;1.5 to 3 times ULN): Reduce initial dose to 7 mg/m2 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (total bilirubin &gt; 3 times ULN): Reduce initial dose to 5 mg/m2.&nbsp;<strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Romidepsin:</strong><br>No dose reduction.</li></ul>', 'Moderate', null, null, null, '<p><strong>Romidepsin: </strong>14 mg/m2 IV on days 1, 8, and 15<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1012, null, 'Belinostat', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Belinostat: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (total bilirubin &lt; 1.5 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (total bilirubin &gt; 1.5 times ULN): Dosage adjustment not provided by the manufacturer.&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Belinostat:</strong><br>If CrCl &gt;39 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≤39 mL/min: Dosage adjustment not provided because of insufficient data</li></ul>', 'Low', null, null, null, '<p><strong>Belinostat: </strong>1,000 mg/m2 IV on days 1-5<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (287, 'Melphalan : Alkeran<br>Prednisone : various', 'Melphalan/Prednisone/Thalidomide (MPT)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Melphalan:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Melphalan:</strong><br>&nbsp;If CrCl 10 to 50 mL/min: Reduce dose to 75%. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose to 50%.</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li></ul>', 'Low', '<b>Melphalan/Prednisone/Talidomide (MPT)</b>', ' $Palumbo A, et al. Lancet 2006;28:367-835.$', null, '<p><strong>Melphalan:</strong> 0.25 mg/kg PO, days 1-4<br><strong>Prednisone:</strong> 1.5 mg/kg PO, days 1-4<br><strong>Thalidomide: </strong>50 - 100 mg/day PO, &nbsp;q daily.</p><p>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (144, 'Gemcitabine : Gemzar, various', 'Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '<b>Gemcitabine</b>', 'Carmichael J, Possinger K, Phillip P, et al. Advanced breast cancer: a phase II trial with gemcitabine. $J Clin Oncol. 1995;13(11):2731-2736.$', null, '<p><strong>Gemcitabine:</strong> 725 mg/m2 IV weekly for 3 weeks.<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (127, 'Capecitabine : Xeloda<br>Ixabepilone : Ixempra', 'Ixabepilone/Capecitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>No dose reduction</li><li><strong>Ixabepilone:</strong><br>Monotherapy:&nbsp;<br>If AST and ALT &lt; 2.5 upper normal limit, and bilirubin &lt; 1 upper normal limit, use 40 mg/m2<br>If AST or ALT &lt; 10 upper normal limit, and bilirubin &lt; 1 upper normal limit, use 32 mg/m2&nbsp;<br>If AST and ALT &lt; 10 upper normal limit, and bilirubin 1.5-3 upper normal limit, use 20-30 mg/m2&nbsp;<br>If AST or ALT &gt; 10 upper normal limit, or bilirubin &gt; 3 upper normal limit, do not use&nbsp;<br>In combination with capecitabine:&nbsp;<br>If AST and ALT &lt; 2.5 upper normal limit, and bilirubin &lt; 1 upper normal limit, use 40 mg/m2&nbsp;<br>If AST or ALT &gt; 2.5 upper normal limit, or bilirubin &gt; 1 upper normal limit, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>If CrCl 30-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Ixabepilone:</strong><br>If CrCl &gt; 30 mL/min, no dose reduction</li></ul>', 'Low', '<b>Ixabepilone/Capecitabine</b>', 'Thomas ES, Gomez HL, Li RK, et al. Ixabepilone plus capecitabine for metastatic breast cancer progressing after anthracycline and taxane treatment. $J Clin Oncol. 2007;25(33):5210-5217.$', null, '<p><strong>Ixabepilone:</strong> 40 mg/m2 IV over 3 hours, day 1<br><strong>Capecitabine:</strong> 1,000 mg/m2 PO bid, days 1-14<br>Repeat every 21 days for up to 8 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (147, 'Paclitaxel : Taxol', 'Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Paclitaxel:</strong><br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Paclitaxel:</strong><br>No dose reduction</li></ul>', 'Low', '<b>Paclitaxel</b>', 'Seidman AD, Tiersten A, Hudis C, et al. Phase II trial of paclitaxel by 3-hour infusion as initial and salvage chemotherapy for metastatic breast cancer. $J Clin Oncol. 1995;13(10):2575-2581.$<br> Smith RE, Brown AM, Mamounas EP, et al. Randomized trial of 3-hour versus 24-hour infusion of high-dose paclitaxel in patients with metastatic or locally advanced breast cancer: National Surgical Adjuvant Breast and Bowel Project Protocol B-26. $J Clin Oncol. 1999;17(11):3403-3411.$', null, '<p><strong>Paclitaxel:</strong> 175 mg/m2 IV over 3 hours on day 1<br>Repeat every 21 days</p><p><strong>OR</strong></p><p><strong>Paclitaxel:</strong> 80-100 mg/m2 IV weekly for 3 weeks<br>Repeat every 4 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (130, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Paclitaxel : Taxol', 'Sequential AC --> T (Weekly)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Paclitaxel:</strong><br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>No dose reduction</li></ul>', 'High ', '<b>Sequential AC --> T</b>', 'Sparano JA, Wang M, Martino S, et al. Weekly paclitaxel in the adjuvant treatment of breast cancer. $N Engl J Med. 2008;238(16):1663-1671.$', null, '<p><strong>Doxorubicin:</strong> 60 mg/m2 over 5-15 minutes, day 1<br><strong>Cyclophosphamide:</strong> 600 mg/m2 IV 30-60 minutes, day 1<br>Repeat every 21 days for 4 cycles &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br><strong>FOLLOWED BY &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong><br><strong>Paclitaxel:</strong> 80 mg/m2 IV over 1 hour<br>Repeat every 7 days for 12 cycles</p>', null);
