insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (133, 'Capecitabine : Xeloda<br>Docetaxel : Taxotere', 'X+T', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>No dose reduction</li><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline Phosphatase &gt;2.5 upper normal limit, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>If CrCl 30-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Docetaxel:</strong><br>No dose reduction</li></ul>', 'Low', '<b>X+T</b>', 'O''Shaughnessy J, Miles D, Vukelja S, et al. Superior survival with capecitabine plus docetaxel combination therapy in anthraccline-pretreated patients with advanced breast cancer: phase III trial results. $J Clin Oncol. 2002;20(12):2812-2823.$', null, '<p><strong>Capecitabine:</strong> 1,250 mg/m2 PO bid, days 1-14<br><strong>Docetaxel:</strong> 75 mg/m2 as a 1-hour IV infusion, day 1<br>Repeat every 21 days. May decrease dose of capecitabine to 825-1,000 mg/m2 PO bid on days 1-14 to reduce the risk of toxicity without compromising clinical efficacy.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (496, 'Palbociclib: Ibrance<br>Fulvestrant: Faslodex', 'Palbociclib/Fulvestrant', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Palbociclib</strong><br>If Child-Pugh A or B, no dose reduction<br>If Child-Pugh C,&nbsp;reduce dose to 75 mg/day</li><li><strong>Fulvestrant</strong>:<br>If SGOT, SGPT, bilirubin, and alkaline phosphatase &lt; 2 upper normal limits, no dose reduction<br>If SGOT, SGPT, bilirubin, and alkaline phosphatase &gt; 2 upper normal limits, unknown</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Fulvestrant</strong>:<br>No dose reduction</li><li><strong>Palbociclib &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li></ul>', 'Low', '', 'Turner NC, Ro J, et al. Palbociclib in Hormone-Receptor-Positive Advanced Breast Cancer. N Engl J Med. 2015;373(3):209-19.', 'METASTATIC', '<p><strong>Palbociclib:</strong>&nbsp;125 mg PO daily for 21 days<br><strong>Fulvestrant:</strong>&nbsp;500 mg IM on days 1 and 15 of the first cycle, then 500 mg IM on day 1 for all subsequent cycles</p><p>Repeat cycle every 28 days.</p>', 16);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (422, 'Carboplatin: Paraplatin<br>Docetaxel: Taxotere<br>Trastuzumab: Herceptin', 'TCH', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Docetaxel</strong>:<br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline phosphatase &gt; 2.5 upper normal limit, do not use</li><li><strong>Trastuzumab: &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on AUC</li><li><strong>Docetaxel</strong>:<br>No dose reduction</li><li><strong>Trastuzumab: &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p>&nbsp;</p><p>&nbsp;<strong>Cardiac Impairment</strong></p><ul><li><strong>Trastuzumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> If<strong>&nbsp;</strong>EF decreases ≥16% &nbsp;from baseline &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong> &nbsp; OR &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;EF below normal limits and ≥10% decrease from baseline &nbsp; &nbsp; &nbsp;Hold treatment for 4 weeks and repeat LVEF every 4 weeks. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Resume treatment if EF returns to normal limits within 4 to 8 weeks and remains at ≤15% decrease from the baseline value. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Stop treatment permanently if EF declines persistently for &gt; 8 weeks or for &gt;3 incidents of interruptions in treatment for cardiomyopathy.</li></ul>', 'Moderate', '', 'Slamon D, Eiermann W, et all. BCIRG 006: 2nd interim analysis phase III randomized trial comparing doxorubicin and cyclophosphamide followed by docetaxel (ACT) with doxorubicin and cyclophosphamide followed by docetaxel and trastuzumab (ACTH) with docetaxel, carboplatin and trastuzumab (TCH) in Her2neu positive early breast cancer patients. Breast Cancer Res Treat. 2006; 100 (Abstract #52).', 'ADJUVANT', '<p><strong>Docetaxel:</strong> 75 mg/m2 IV on&nbsp;day 1<br><strong>Carboplatin:</strong> AUC of 6, IV on&nbsp;day 1<br><strong>Trastuzumab:&nbsp;</strong>4 mg/kg IV loading dose and then 2 mg/kg IV weekly during chemotherapy</p><p>Repeat every 21 days for 6 cycles, followed by</p><p><strong>Trastuzumab:</strong>&nbsp;6 mg/kg IV on day 1</p><p>Repeat every 21 days for 1 year.</p>', 16);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1021, null, 'Abraxane', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Abraxane:&nbsp;</strong><br>If mild impairment (Child-Pugh class A): Dose at 260 mg/m2. &nbsp; &nbsp; &nbsp; If moderate impairment (Child-Pugh class B): Dose at 200 mg/m2. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child-Pugh class C): Use is not recommended&nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Abraxane:&nbsp;</strong><br>If CrCl ≥30 mL/minute: No dosage adjustment necessary. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/minute: There are no dosage adjustments provided in the manufacturer''s labeling (insufficient data).</li></ul>', 'Low', null, null, null, '<p><strong>Abraxane: </strong>260 mg/m2 IV on day 1.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1125, null, 'PD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pomalidomide:</strong><br>Avoid POMALYST in patients with serum bilirubin greater than 2.0 mg/dL and AST/ALT greater than 3.0 x ULN.</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pomalidomide:</strong><br>Avoid Pomalidamide in patients with serum creatinine greater than 3.0 mg/dL</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Pomalidomide:</strong> 4 mg/day PO on days 1-21<br><strong>Dexamethasone:</strong> 40 mg/day PO on day 1<br>Repeat cycle every 28 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1130, null, 'RMPT', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>No dose reduction</li><li><strong>Melphalan:</strong><br>No dose reduction</li><li><strong>Prednisone:</strong><br>No dose reduction</li><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li><li><strong>Melphalan:</strong><br>&nbsp;If CrCl 10 to 50 mL/min: Reduce dose to 75%. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose to 50%.</li><li><strong>Prednisone:</strong><br>No dose reduction</li><li><strong>Thalidomide:</strong>&nbsp;<br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Lenalidomide:</strong> 10 mg PO on days 1-21<br><strong>Melphalan:</strong> 0.18 mg/kg PO on days 1-4<br><strong>Prednisone:</strong> 2 mg/kg PO on days 1-4<br><strong>Thalidomide: </strong>50 mg PO daily on days 1-28</p><p>Repeat cycle every 28 days for 6 cycles followed by maintenance lenalidomide<br>10 mg PO on days 1-21 until progression or toxicity.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (420, 'Cyclophosphamide: Cytoxan<br>Doxorubicin: Adriamycin, various<br>Paclitaxel: Taxol<br>Trastuzumab: Herceptin', 'AC/Paclitaxel/Trastuzumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide</strong>:<br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li><strong>Trastuzumab: &nbsp; &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dosage modification.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li><li><strong>Trastuzumab: &nbsp; &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dosage modification. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Cardiac Impairment</strong></li><li><strong>Trastuzumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> If<strong> </strong>EF decreases ≥16% &nbsp;from baseline &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong> &nbsp; OR &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;EF below normal limits and ≥10% decrease from baseline &nbsp; &nbsp; &nbsp;Hold treatment for 4 weeks and repeat LVEF every 4 weeks. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Resume treatment if EF returns to normal limits within 4 to 8 weeks and remains at ≤15% decrease from the baseline value. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Stop treatment permanently if EF declines persistently for &gt; 8 weeks or for &gt;3 incidents of interruptions in treatment for cardiomyopathy.</li></ul>', 'High', '', 'Romond EH, Perez EA, et al. Trastuzumab plus adjuvant chemotherapy for operable HER2-positive breast cancer. N Engl J Med. 2005;353(16):1673-84.', 'ADJUVANT', '<p><strong>Doxorubicin:</strong>&nbsp;60 mg/m2&nbsp;IV on day 1<br><strong>Cyclophosphamide:</strong>&nbsp;600 mg/m2&nbsp;IV on day 1</p><p>Repeat every 21 days for 4 cycles, followed by</p><p><strong>Paclitaxel:</strong>&nbsp;80 mg/m2&nbsp;IV over 1 hour on day 1<br><strong>Trastuzumab:</strong>&nbsp;4 mg/kg IV loading dose, then 2mg/kg IV weekly</p><p>Repeat every 7 days for 12 cycles, followed by</p><p><strong>Trastuzumab:</strong>&nbsp;2 mg/kg IV weekly</p><p>Repeat every 7 days for 40 cycles.</p>', 16);
