insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (775, null, 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (total bilirubin &lt;1 to 1.5 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (total bilirubin &gt;1.5 times ULN): Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Pembrolizumab:&nbsp;</strong>200 mg<strong> </strong>IV on day 1</p><p>Repeat cycles every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1035, null, 'Methotrexate', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Low', null, null, null, '<p><strong>Methotrexate:</strong> 40 mg/m2 IV or IM weekly</p><p>Repeat cycle every week.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (862, null, 'Lenvatinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenvatinib:&nbsp;</strong><br>If mild impairment (Child-Pugh class A): 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If moderate impairment (Child-Pugh class B): 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; If Severe impairment (Child-Pugh class C): Reduce dose to 14 mg once daily.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenvatinib:&nbsp;</strong><br>If CrCl ≥30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Reduce dose to 14 mg once daily.</li></ul>', 'High', null, null, null, '<p><strong>Lenvatinib: </strong>24 mg PO daily<br>Continue treatment until disease progression</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (695, '', 'Nilotinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Nilotinib:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Nilotinib:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Nilotinib:&nbsp;</strong>400-600 mg PO twice daily</p><p>Continue treatment until disease progression.</p>', 8);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (773, null, 'Carboplatin + Fluorouracil', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use<br>No dose reduction for infusional administration</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% of the usual dose. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% of the usual dose.</li><li><strong>Fluorouracil:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Carboplatin:</strong> AUC of 5, IV on day 1<br><strong>Fluorouracil:</strong> 1,000 mg/m2/day IV continuous infusion on days 1-4</p><p><br>Repeat cycle every 21 days.<br>&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (772, null, 'Cisplatin + radiation (Nasopharyngeal)', '<p><strong>Hepatic Impairment:</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use<br>No dose reduction for infusional administration</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug.</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin:</strong> 100 mg/m2 IV on days 1, 22 and 43.&nbsp;</p><p><strong>Radiation therapy:</strong> 7,000 cGy in 180-200 cGy fractions</p><p>After the completion of chemoradiotherapy, it should be followed by</p><p><strong>Cisplatin</strong>: 80 mg/m2 IV on day 1</p><p><strong>5-Fluorouracil:</strong> 1000 mg/m2/day as IV continuous infusion on days 1-4</p><p>Repeat every 28 days for 3 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1040, null, 'BV', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin</strong>:<br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin</strong><br>If CrCl &gt;50 mL/min: No dose reduction<br>If CrCl 40-50 mL/min: 70% of normal dose<br>If CrCl 30-40 mL/min: 60% of normal dose<br>If CrCl 20-30 mL/min: 55% normal dose<br>If CrCl 10-20 mL/min: 45% normal dose<br>If CrCl 5-10 mL/min: 40% normal dose</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Bleomycin:</strong> 10 U/m2 IV on days 1 and 15<br><strong>Vincristine:</strong> 1.4 mg/m2 IV on days 1 and 15 (maximum, 2 mg)<br>Repeat cycle every 2 weeks</p>', null);
