insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1170, null, 'Paclitaxel + Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Paclitaxel:</strong> 100 mg/m2 IV on days 1,8, and 15<br><strong>Gemcitabine: </strong>1,000 mg/m2 IV on days 1,8, and 15<br>Repeat cycle every 28 days for a total of 6 cycles.</p>', null);
