insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (322, 'Cytarabine : Ara-C<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'VCR + Prednisone + Pegaspargase + Cytarabine + MTX', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Prednisone:</strong><br>No dose reduction</li><li><strong>Pegaspargase:</strong><br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><p>&nbsp;</p><ul><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone:</strong><br>No dose reduction</li><li><strong>Pegaspargase:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Moderate', '<b>VCR/Prednisone/Pegaspargase/Cytarabine/MTX</b>', 'Avramis VI, Sencer S, Periclou AP, et al. A randomized comparison of native Escherichia coli asparaginase and polyethylene glycol conjugated asparaginase for treatment of children with newly diagnosed standard-risk acute lymphoblastic leukemia: a Children''s Cancer Group Study. $Blood. 2002;99(6):1986-1994.$ $Erratum in: Blood. 2002; 100(6):1531.$', null, '<p><strong>Vincristine:</strong> 1.5 mg/m2 IV, days 0, 7, 14, 21<br><strong>Prednisone:</strong> 40 mg/m2 PO, days 0-28, then 10-day taper<br><strong>Pegaspargase:</strong> 2,500 units/m2 IM, day 3<br><strong>Cytarabine:</strong> 30, 50, or 70 mg IT, age-adjusted, day 0<br><strong>Methotrexate:</strong> 8, 10, or 12 mg IT, age-adjusted, days 7, 28</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (326, 'Cytarabine : Ara-C<br>Hydrocortisone : various', 'TIT (CNS Prophylaxis)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Hydrocortisone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Hydrocortisone:</strong><br>No dose reduction</li></ul>', 'Moderate', '<b>TIT (CNS Prophylaxis)</b>', 'Lauer SJ, Camitta BM, Leventhal BG, et al. Intensive alternating drug pairs for treatment of hgh-risk chidhood acute lymphoblastic leukemia. Apediatric Oncology Group pilot study. $Cancer. 1993;71(9):2854-2861.$', null, '<p><strong>Regimen: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Age 1 year &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Age 2 year &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Age ≥ 3 years</strong><br><strong>Methotrexate: &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> 10 mg &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;12.5 mg &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 15 mg<br><strong>Cytarabine:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 20 mg &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 25 mg &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;30 mg<br><strong>Hydrocortisone:</strong> &nbsp; &nbsp; &nbsp; 10 mg &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;12.5 mg &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;15 mg<br>Administer IT on day 1, then at weeks 1, 2, 3, 6, 11, 16, 21, 26, 31, then every 12 weeks</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1178, null, 'SNCCL/B-ALL', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL: 50% of usual dosage.<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL: Do not use</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 5 mg/dL: Do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 3 mg/dL: 50% of usual dosage.</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: 50% of usual dosage.<br>If bilirubin 3.1-5 mg/dL: 25% of usual dosage.<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min: 75% of usual dosage.<br>If CrCl &lt; 10 mL/min: 50% of usual dosage.</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min: 50% of normal dosage.<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'High', null, null, null, '<p>INITIAL PHASE<br><strong>Cytarabine:</strong> 50 mg/m2/day (maximum, 50 mg/d) IT, days 1, 2, 3, 11<br><strong>Methotrexate:</strong> 12 mg/m2/day (maximum, 12 mg/d) IT, days 4, 11<br><strong>Cyclophosphamide:</strong> 300 mg/m2 IV push every 12 hours for 6 doses, days 1-3<br><strong>Doxorubicin:</strong> 50 mg/m2 IV, day 4<br><strong>Vincristine:</strong> 1.5 mg/m2/day IV, days 4, 11<br>Alternating with:<br><br>SECONDARY PHASE<br><strong>Cytarabine:</strong> 50 mg/m2 (maximum, 50 mg) IT, day 1<br><strong>Methotrexate:</strong> 12 mg/m2 (maximum, 12 mg) IT, day 1<br><strong>Methotrexate:</strong> 200 mg/m2 IV bolus, then 800 mg/m2 IV continuous infusion for 24 hours.<br><strong>Cytarabine:</strong> 3,000 mg/m2 IV over 3 hours every 12 hours for 4 doses, days 2, 3<br><strong>Leucovorin calcium:</strong> 30 mg/m2 IV at hour 42, then 3 mg/m2 IV at hours 54, 66, 78</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (303, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16', 'A3', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', '<b>A<sub>3</sub></b>', 'Kaneko M, Tsuchida Y, Mugishima H, et al. Intensified chemotherapy increases the survival rates in patients with stage 4 neuroblastoma with MYCN amplification. $J Pediatr Hematol Oncol. 2002;24(8):613-621.$', null, '<b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup>/d IV, days 1, 2<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 3<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d CIVI, days 1-5', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1181, null, 'IFoVP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Etoposide</strong>:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Etoposide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Ifosfamide:</strong> 1,800 mg/m2/day IV, days 1-5<br><strong>Etoposide:</strong> 100 mg/m2/d IV over 1 hour, days 1-5<br><strong>Mesna:</strong> 2,880 mg/m2/day IV, days 1-5<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (315, 'Cyclophosphamide : Cytoxan<br>Topotecan : Hycamtin', 'Topo + CTX', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Topotecan:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Topotecan:</strong><br>If CrCl &gt; 40 mL/min, no dose reduction&nbsp;<br>If CrCl 20-39 mL/min, reduce to 0.75 mg/m2</li></ul>', 'High', '<b>Topo/CTX</b>', 'Saylors RL, Stewart CF, Zamboni WC, et al. Phase I study of topotecan in combination with cyclophosphamide in pediatric patients with malignant solid tumors: a Pediatric Oncolgy Group study. $J Clin Oncol. 1998;16(3):945-952.$', null, '<p><strong>Cyclophosphamide:</strong> 250 mg/m2/day IV, days 1 to 5.<br>Followed by<br><strong>Topotecan:</strong> 0.6-0.75 mg/m2/day IV as an escalating dose, days 1-5</p><p><strong>Filgrastim:</strong> 5 mcg/kg SC daily beginning on day 6 until ANC ≥1,500/mm3 after the time of expected nadir</p><p>Repeat cycle every 21 days until disease progression and unacceptable toxicity.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1182, null, '6-MP orally MTX', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 5 mg/dL: Do not use</li><li><strong>6-Mercaptopurine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min: 50% of normal dosage.<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>6-Mercaptopurine:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Mercaptopurine:</strong> 25 mg/m2/day PO, days 1-56</p><p><strong>Methotrexate:</strong> 5000 mg/m2 IV over 24 hours on days 8,22, 36, 50.&nbsp;</p><p><strong>Methotrexate:</strong> 12 mg/day IT (adjust dose for children less than 3 years)days 8,22, 36, 50.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (336, '', 'MidAC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Mitoxantrone</strong><br>There are no pediatric-specific recommendations</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Mitoxantrone</strong><br>There are no pediatric-specific recommendations</li></ul>', 'Moderate', '<b>MidAC</b>', 'Stevens RF, Hann IM, Wheatley K, Gray RG. Marked improvements in outcome with chemotherapy alone in paediatric acute myeloid leukaemia: results of the United Kingdom Medical Research Council''s 10th AML trial. $Br J Haematol. 1998;101(1):130-140.$', null, '<p><strong>Mitoxantrone:</strong> 10 mg/m2 IV (short infusion), days 1-5<br><strong>Cytarabine:</strong> 1,000 mg/m2 IV (2-hour infusion), every 12 hours, days 1-3</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (305, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16', 'Cisplatin + Doxorubicin + Cyclophosphamide + Etoposide( High risk Neuroblastoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', '<b>High-risk neuroblastoma</b>', 'Matthay KK, Villablanca JG, Seeger RC, et al. Treatment of high-risk neuroblastoma with intensive chemotherapy, radiotherapy, autologous bone marrow transplantation, and 13-cisretinoic acid. $N Engl J Med. 1999;341(16):1165-1173.$', null, '<p><strong>Cisplatin:</strong> 60 mg/m2 IV over 6 hours, day 0<br><strong>Doxorubicin:</strong> 30 mg/m2 IV, day 2<br><strong>Etoposide:</strong> 100 mg/m2/day IV, days 2, 5<br><strong>Cyclophosphamide:</strong> 1,000 mg/m2/day IV, days 3, 4<br>Repeat every 28 days for a total of 5 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1183, null, 'MAE 3+10+5', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Mitoxantrone</strong><br>There are no pediatric-specific recommendations</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Mitoxantrone</strong><br>There are no pediatric-specific recommendations</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'Moderate', null, null, null, '<p><strong>Mitoxantrone:</strong> 12 mg/m2 IV days 1, 3, 5.<br><strong>Cytarabine:</strong> 100 mg/m2 IV every 12 hours, days 1-10</p><p><strong>Etoposide:</strong> 100 mg/m2 IV, days 1 to 5.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1184, null, 'CLASP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>L-Asparaginase: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Do not use in patients with severe hepatic impairment.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>L-Asparaginase:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Cytarabine:</strong> 3,000 mg/m2 every 12 hours IV, days 1, 2, 8, 9.<br><strong>Asparaginase:</strong> 6,000 units/m2 SC, day 2, 9.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1185, null, 'ADE 10+3+5', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Daunorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: &nbsp;Reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL: Reduce dose by 50%<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL: &nbsp;Reduce dose by 25%&nbsp;<br>If bilirubin &gt; 3 mg/dL: Reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li></ul>', 'Moderate', null, null, null, '<p><strong>Cytarabine:</strong> 100 mg/m2 every 12 hours IV, days 1-10</p><p><strong>Daunorubicin:&nbsp;</strong> 50 mg/m2/day IV, days 1, 3, 5</p><p><strong>Etoposide:</strong> 100 mg/m2 IV, days 1- 5.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (310, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various', 'MTX-CDDP/Adr', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Leucovorin:</strong><br>No dose reduction</li></ul>', 'High', '<b>MTX-CDDP/Adr</b>', 'Weiner MA, Harris MB, Lewis M, et al. Neoadjuvant high-dose methotrexate, cisplatin, and doxorubicin for the management of patients with nonmetastatic osteosarcoma. $Cancer Treat Rep. 1986;70(12):1431-1432.$', null, '<p><strong>Methotrexate:</strong> 12,000 mg/m2 IV weekly for 2 weeks; escalate dose by 1,000 mg/m2 in subsequent courses until peak level of 10 to the power -8 M is reached (reduce dose to 8,000 mg/m2 IV for patients &gt;12 years)<br><strong>Leucovorin calcium:</strong> 20 mg/m2 IV every 3 hours for 8 doses beginning 16 hours after completion of methotrexate, then every 6 hours PO for 8 doses<br><strong>ALTERNATING WITH:</strong><br><strong>Cisplatin:</strong> 75 mg/m2 IV, day 15 of cycles 1-7, then 120 mg/m2 for cycles 8-10<br><strong>Doxorubicin:</strong> 25 mg/m2/day IV, days 15-17 of cycles 1-7</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (847, null, 'Vincristine/ Carboplatin/ Etoposide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min:&nbsp;Reduce dose by 50%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min:&nbsp;Reduce dose by 75%</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Vincristine:</strong> 0.05 mg/kg (for age &lt;12 months) <strong>OR</strong> 1.5-2 mg/m2 (for age &gt;12 months) on day 0<br><strong>Carboplatin:</strong> 20 mg/kg (for age &lt;12 months) &nbsp;<strong>OR </strong>550-600 mg/m2<strong> </strong>(for age &gt;12 months) on day 0<br><strong>Etoposide phosphate: </strong>5 mg/kg (for age &lt;12 months) or 150 mg/m2 (for age &gt;12 months) on day 0 and 1.</p><p>Repeat cycle every 28 days for 6 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1180, null, 'ICE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Etoposide</strong>:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose by 25%</li><li><strong>Carboplatin</strong>:<br>Dosing based on&nbsp;<strong>mg/m2</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min:&nbsp;Reduce dose by 50%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min:&nbsp;Reduce dose by 75%</li><li><strong>Etoposide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', null, null, null, '<p><strong>Ifosfamide:</strong> 1,800 mg/m2/day IV over 1 hour, days 0 to 4<br><strong>Carboplatin:</strong> 400 mg/m2/day IV over 1 hour, days 0 and 1<br><strong>Etoposide: </strong>100 mg/m2/day IV over 1 hour, days 0 to 4</p><p><strong>Filgrastim:</strong> 5 to 10 mcg/kg/day SC, days 6 to 18 if ANC ≥1,000/mm3 or until post nadir ANC ≥1,000/mm3</p><p>Repeat cycle every 21 days for up to 12 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1174, null, 'CDDP/VP-16', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Etoposide</strong>:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: Reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Etoposide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin:&nbsp;</strong>90 mg/m2&nbsp;IV on day 1</p><p><strong>Etoposide:&nbsp;</strong>150 mg/m2/day&nbsp;IV on day 3, 4</p><p>Repeat cycles every 21 days for a total of 4 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1186, null, 'HIT-SKK', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cyclophosphamide</strong>:<br>No pediatric-specific dosing.</li><li><strong>Carboplatin:</strong><br>No pediatric-specific dosing.</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><p>&nbsp;</p><ul><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cyclophosphamide</strong>:<br>No pediatric-specific dosing.</li><li><strong>Carboplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% dosage.</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', null, null, null, '<p><i>WEEK 1</i></p><p><strong>Methotrexate: </strong>2 mg/day intraventricular, days 1-4<br><strong>Cyclophosphamide:</strong> 800 mg/m2/day IV, days 1-3<br><strong>Vincristine:</strong> 1.5 mg/m2 IV, day 1<br><i>WEEKS 3 AND 5</i><br><strong>Methotrexate: </strong>2 mg/day intraventricular, days 1-2<br><strong>Methotrexate: </strong>5 g/m2 CIVI<br><strong>Vincristine: </strong>1.5 mg/m2, day1<br><i>WEEK 7</i><br><strong>Methotrexate: </strong>2 mg/day intraventricular, days 1-4<br><strong>Carboplatin:</strong> 200 mg/m2 IV, days 1-3<br><strong>Etoposide: </strong>150 mg/m2 IV, days 1-3<br>Repeat for 3 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (851, null, 'ABVD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>No dose reduction</li><li><strong>Dacarbazine:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vinblastine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Dacarbazine:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vinblastine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Doxorubicin:</strong> 25 mg/m2/day IV, days 1, 15<br><strong>Bleomycin:</strong> 10 units/m2/day IV, days 1, 15<br><strong>Vinblastine:</strong> 6 mg/m2/day IV, days 1, 15<br><strong>Dacarbazine:</strong> 375 mg/m2/day IV, days 1, 15<br>Repeat cycle every 14 days for a total of 12 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1179, null, 'A1', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', null, null, null, '<p><strong>Cyclophosphamide</strong> 1,200 mg/m2 IV, days 1.<br><strong>Doxorubicin</strong> 40 mg/m2 IV, day 3<br><strong>Etoposide</strong> 100 mg/m2/day IV, days 1-5<br><strong>Cisplatin</strong> 90 mg/m2 IV on day 5.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1187, null, 'ABVE-PC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL: 50% of usual dosage.<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL: Do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: 50% of usual dosage.<br>If bilirubin 3.1-5 mg/dL: 25% of usual dosage.<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Bleomycin:</strong><br>There are no pediatric-specific recommendations</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min: 75% of usual dosage.<br>If CrCl &lt; 10 mL/min: 50% of usual dosage.</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Bleomycin:</strong><br>There are no pediatric-specific recommendations</li></ul>', 'High', null, null, null, '<p><strong>Doxorubicin:</strong> 30 mg/m2 IV, days 0,1<br><strong>Bleomycin:</strong> 5 units/m2 IV/SQ, day 0<br><strong>Bleomycin:</strong> 10 units/m2 IV/SQ, day 7<br><strong>Vincristine:</strong> 1.4 mg/m2 IV days 0, 7 (maximum 2.8 mg)<br><strong>Etoposide:</strong> 75 mg/m2 IV, days 0-4<br><strong>Prednisone:</strong> 40 mg/m2 PO, days 0-7<br><strong>Cyclophosphamide:</strong> 800 mg/m2 IV, day 0<br><strong>Filgrastim: </strong>5 mcg/kg IV OR SQ (day 5 until neutrophil recovery; held day 7)</p>', null);
